import React from 'react'
import { useState } from 'react'
import { useDispatch } from 'react-redux'
import { registerUser } from '../../../_actions/user_action';
import { useNavigate } from 'react-router-dom';


function RegisterPage() {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const [Email, setEmail] = useState("");
  const [Name, setName] = useState("");
  const [Password, setPassword] = useState("");
  const [ConfirmPassword, setConfirmPassword] = useState("");
  const [ApiKey, setApiKey] = useState("");
  const [ConfirmApiKey, setConfirmApiKey] = useState("");


  const onEmailHandler = (event) => {
    setEmail(event.currentTarget.value)
  }

  const onNameHandler = (event) => {
    setName(event.currentTarget.value)
  }

  const onPasswordHandler = (event) => {
    setPassword(event.currentTarget.value)
  }

  const onConfirmPasswordHandler = (event) => {
    setConfirmPassword(event.currentTarget.value)
  }

  const onApiKeyHandler = (event) => {
    setApiKey(event.currentTarget.value)
  }

  const onConfirmApiKeyHandler = (event) => {
    setConfirmApiKey(event.currentTarget.value)
  }

  const onSubmitHandler = (event) => {
    event.preventDefault(); 

    if(Password !== ConfirmPassword){
      return alert("비밀번호가 일치하지 않습니다.")
    }
    else if(ApiKey !== ConfirmApiKey){
      return alert("ApiKey가 일치하지 않습니다.")
    }
    let body = {
      name: Name,
      email: Email,
      password: Password
    }

    console.log(Name);
    console.log(Email);
    console.log(Password);

    dispatch(registerUser(body))
      .then(response => {
        if(response.payload.success) {
          navigate('/') // landing page 로 
        }
        else {
          alert(response.payload.message)
        }
      })
  }

  return (
    <div style={{
      display: 'flex', justifyContent: 'center', alignItems: 'center',
      width: '100%', height: '100vh'
    }}>
        <form style={{ display: 'flex', flexDirection: 'column'}}
          onSubmit={onSubmitHandler}
        >
          <label>Email</label>
          <input type="email" value={Email} onChange={onEmailHandler}></input>

          <label>Name</label>
          <input type="" value={Name} onChange={onNameHandler}></input>

          <label>Password</label>
          <input type="password" value={Password} onChange={onPasswordHandler}></input>

          <label>Confirm Password</label>
          <input type="password" value={ConfirmPassword} onChange={onConfirmPasswordHandler}></input>

          <label>UpBit API KEY</label>
          <input type="password" value={ApiKey} onChange={onApiKeyHandler}></input>

          <label>Confirm UpBit API KEY</label>
          <input type="password" value={ConfirmApiKey} onChange={onConfirmApiKeyHandler}></input>

          <br />
          <button>
            회원가입
          </button>
        </form>
    </div>
  )
}

export default RegisterPage
