import axios from 'axios';
import {
    TRADING_START
} from './types';

export function tradingStart(dataToSubmit) {

    const request = axios.post('/api/users/coin/tarding', dataToSubmit)
      .then(response =>  response.data )

      return{
        type: TRADING_START,
        payload: request
      }
}
